export const GLSLX_SOURCE_MAIN = "precision highp float;uniform sampler2D p;uniform vec2 g,q;uniform vec3 L,r;uniform vec4 i[10];uniform float s,t,u,M,v,w,x,y,z;mat3 A(float a,float b){float c=cos(a),d=cos(b),e=sin(a),f=sin(b);return mat3(d,f*e,-f*c,0.,c,e,f,d*-e,d*c);}vec4 N(float B,float C,float D,float E){vec2 a=(gl_FragCoord.xy/g*2.-1.)/u-q*vec2(1.,-1.)/g-vec2(B,-C);a.x*=g.x/g.y;float b=dot(a,a);vec4 F=vec4(0.);float j=0.;if(b<=.64){for(int c=0;c<2;c++){vec4 m=vec4(0.);float n;vec3 o=vec3(0.,0.,1.),d=normalize(vec3(a,sqrt(.64-b)));d.z*=c>0?-1.:1.,o.z*=c>0?-1.:1.;vec3 G=d*A(t,s),e=G;n=0.;float k=asin(e.y),f=acos(-e.x/cos(k));f=e.z<0.?-f:f;float H=max(texture2D(p,vec2(f*.5/3.141593,-(k/3.141593+.5))).x,z),I=smoothstep(8e-3,0.,n),h=dot(d,o),J=pow(h,w)*v*D,l=H*I*J+E,O=mix((1.-l)*pow(h,.4),l,x)+.1;m+=vec4(L*O,1.),m.xyz+=pow(1.-h,4.)*r,F+=m*(1.+(c>0?-y:y))/2.;}j=pow(dot(normalize(vec3(-a,sqrt(1.-b))),vec3(0.,0.,1.)),4.)*smoothstep(0.,1.,.2/(b-.64));}else{float K=sqrt(.2/(b-.64));j=smoothstep(.5,1.,K/(K+1.));}return F+vec4(j*r,j);}void main(){vec4 b=vec4(0.);for(int a=0;a<10;a++){if(a>int(M-.1))break;b+=N(i[a].x,i[a].y,i[a].z,i[a].w);}gl_FragColor=b;}"

